mapper.select:
/* common.dao.CommonMapper.xml selCodeSubDepth */ SELECT A.code, CONCAT('[', B.cname, '] ', A.cname) cname FROM ( SELECT * FROM T_CODE WHERE 1=1 AND root = #{code} AND heigher != #{code}
AND heigher = #{higher}
) A, ( SELECT B.* FROM T_CODE_GRP A, T_CODE B WHERE A.CODE_ID = B.ROOT AND A.USED_YN = 'Y' AND B.SEARCH_YN = 'Y' AND root = #{code} AND depth =
(SELECT MIN(CAST(depth AS UNSIGNED)-1) FROM T_CODE WHERE heigher = #{higher})
('0')
) B WHERE A.heigher = B.code

mapper.insert:
/* common.dao.CommonMapper.xml insAttachFile */ INSERT INTO T_ATTACH_FILE (
ATTACH_ID,
ATTACH_SEQ, FILE_ORIGIN_NAME, FILE_NAME, FILE_SIZE, FILE_PATH, FILE_CONTENT_TYPE, REG_ID, REG_DT, UPD_ID, UPD_DT ) SELECT #{attachId},
NVL((MAX(ATTACH_SEQ) + 1), 1), #{fileName}, #{fileChangeName}, #{fileSize}, #{filePath}, #{fileContentType}, #{userId}, DATE_FORMAT(NOW(), '%Y%m%d%H%i%s'), #{userId}, DATE_FORMAT(NOW(), '%Y%m%d%H%i%s') FROM T_ATTACH_FILE WHERE ATTACH_ID = #{attachId}

mapper.delete:
/* common.dao.CommonMapper.xml delAttachFile */
DELETE	FROM T_ATTACH_FILE
WHERE	ATTACH_ID = #{attachId}
  AND	ATTACH_SEQ = #{attachSeq}

mapper.update:
/* common.dao.CommonMapper.xml updIdGenNextNo */
UPDATE	T_ID_GEN
SET		CURR_NO = IFNULL(CURR_NO, 0) + 1
WHERE	TABLE_NAME = #{tableName}

/* common.dao.CommonMapper.xml updIdGenCycle */
UPDATE	T_ID_GEN
SET		CURR_YEAR = #{currYear},
		CURR_MONTH = #{currMonth},
		CURR_DAY = #{currDay}, 
		CURR_NO = #{currNo}
WHERE	TABLE_NAME = #{tableName}
