mapper.insert:
/* portal.diagreq.dao.DiagReqMapper.xml insDiagHist */
INSERT INTO T_DIAG_HIST 
	(
		DIAG_ID,
		REG_DT,
		UPD_DESC,
		REG_ID
	) VALUES ( 
		#{diagId},
		DATE_FORMAT(NOW(), '%Y%m%d%h%m%s'),
		#{updDesc},
		#{userId}
	)

mapper.select:
/* portal.diagreq.dao.DiagReqMapper.xml selSvcDiagReqList */ SELECT COUNT(*) OVER () RNUM_CNT,
SVC_ID, SVC_NAME, FN_CODE_NAME('0003',A.USE_YN) USE_YN_NAME, (SELECT CONCAT(REQ_DT,',',FN_USER_NAME(req_user_id),',',FN_CODE_NAME('0001',STATUS_CODE)) FROM T_DIAG WHERE SVC_ID = A.SVC_ID AND DIAG_TYPE_CODE = '01' ORDER BY REQ_DT DESC LIMIT 1) INFRA_REQ_INFO, (SELECT CONCAT(REQ_DT,',',FN_USER_NAME(req_user_id),',',FN_CODE_NAME('0001',STATUS_CODE)) FROM T_DIAG WHERE SVC_ID = A.SVC_ID AND DIAG_TYPE_CODE = '03' ORDER BY REQ_DT DESC LIMIT 1) MOBILE_REQ_INFO, (SELECT CONCAT(REQ_DT,',',FN_USER_NAME(req_user_id),',',FN_CODE_NAME('0001',STATUS_CODE)) FROM T_DIAG WHERE SVC_ID = A.SVC_ID AND DIAG_TYPE_CODE = '04' ORDER BY REQ_DT DESC LIMIT 1) SRC_REQ_INFO, (SELECT CONCAT(REQ_DT,',',FN_USER_NAME(req_user_id),',',FN_CODE_NAME('0001',STATUS_CODE)) FROM T_DIAG WHERE SVC_ID = A.SVC_ID AND DIAG_TYPE_CODE = '05' ORDER BY REQ_DT DESC LIMIT 1) IOT_REQ_INFO FROM T_SVC A WHERE 1 = 1 AND A.SVC_ID IN (SELECT SVC_ID FROM T_SVC_USER WHERE USER_ID = #{userId})
AND LOWER(TRIM(SVC_NAME)) LIKE CONCAT('%',LOWER(TRIM(#{searchSvcName})),'%')
ORDER BY A.SVC_ID DESC
LIMIT #{startPageNo},#{pageSize}

/* portal.diagreq.dao.DiagReqMapper.xml selTypeDiagReqList */ SELECT COUNT(*) OVER () RNUM_CNT,
B.DIAG_ID, B.DIAG_NAME, A.SVC_ID, A.SVC_NAME, FN_CODE_NAME('0002',B.DIAG_TYPE) DIAG_TYPE_NAME, FN_USER_NAME(B.REQ_USER_ID) REQ_USER_NAME, FN_CODE_NAME('0001',B.STATUS_CODE) STATUS_CODE_NAME, CASE WHEN B.STATUS_CODE>='02' THEN STR_TO_DATE(B.REQ_DT,'%Y%m%d') WHEN B.STATUS_CODE = '01' THEN '0' ELSE NULL END AS REQ_DT, -- 신청완료 CASE WHEN B.STATUS_CODE>='03' THEN STR_TO_DATE(B.ACCEPT_DT,'%Y%m%d') WHEN B.STATUS_CODE = '02' THEN '0' ELSE NULL END AS ACCEPT_DT, -- 접수 CASE WHEN B.STATUS_CODE>='04' THEN STR_TO_DATE(B.PLAN_DT,'%Y%m%d') WHEN B.STATUS_CODE = '03' THEN '0' ELSE NULL END AS PLAN_DT, -- 계획 CASE WHEN B.STATUS_CODE>='05' THEN STR_TO_DATE(B.DIAG_DT,'%Y%m%d') WHEN B.STATUS_CODE = '04' THEN '0' ELSE NULL END AS DIAG_DT, -- 진단 CASE WHEN B.STATUS_CODE>='06' THEN STR_TO_DATE(B.REDIAG_DT,'%Y%m%d') WHEN B.STATUS_CODE = '05' THEN '0' ELSE NULL END AS REDIAG_DT -- 취약점 조치 FROM T_SVC A, T_DIAG B WHERE A.SVC_ID = B.SVC_ID AND A.SVC_ID IN (SELECT SVC_ID FROM T_SVC_USER WHERE USER_ID = #{userId})
AND LOWER(TRIM(A.SVC_NAME)) LIKE CONCAT('%',LOWER(TRIM(#{searchSvcName})),'%')
AND LOWER(TRIM(B.DIAG_NAME)) LIKE CONCAT('%',LOWER(TRIM(#{searchDiagName})),'%')
AND B.DIAG_TYPE = #{searchDiagType}
AND B.STATUS_CODE = #{searchStatusCode}
AND FN_USER_NAME(B.REQ_USER_ID) = #{searchReqUserName}
AND B.REQ_DT >= CONCAT(#{searchStartDate},'000000')
AND B.REQ_DT <= CONCAT(#{searchEndDate},'000000')
LIMIT #{startPageNo},#{pageSize}
